<?phpinclude 'views/view.php';class MovieView extends View{    public function index(){        $this->movie();    }    public function movie(){        $art=$this->loadModel('Movie');        $this->set('movies', $art->getMovie());        $this->set('category', $art->getCategory());        $this->render('header');        $this->render('navbarMovie');        $this->render('container');        $this->render('skrypt');    }    public function search($name, $wor, $typ, $datF, $datT, $ratF, $ratT, $lonF, $lonT, $wer, $site, $sort, $how){        $art=$this->loadModel('Movie');        $this->set('movies', $art->getSearchMovies($name, $wor, $typ, $datF, $datT, $ratF, $ratT, $lonF, $lonT, $wer, $site, $sort, $how));        $this->set('category', $art->getCategory());        $this->render('header');        $this->render('navbarMovie');        $this->render('container');        $this->render('skrypt');    }}